#[derive(Debug, PartialEq, Clone)]
struct Loc {
    passable: bool,
    dist: i32,
    is_portal: bool,
    portal_tag: String,
    portal_coord: (usize, usize),
}

fn recursive_fill(map: &mut Vec<Vec<Loc>>, cur_x: usize, cur_y: usize) {
    let offsets: Vec<(i32, i32)> = vec![(1, 0), (-1, 0), (0, -1), (0, 1)];

    let d = map[cur_y][cur_x].dist;

    for (dx, dy) in &offsets {
        let mut ix = ((cur_x as i32) + dx) as usize;
        let mut iy = ((cur_y as i32) + dy) as usize;

        if map[iy][ix].is_portal {
            let non_moving_portal_coords_fuck_rust = &map[iy][ix].portal_coord;
            ix = non_moving_portal_coords_fuck_rust.0;
            iy = non_moving_portal_coords_fuck_rust.1;

            //println!(
            //    "[{}, {}], portal: {} -> {:?}",
            //    ix, iy, map[iy][ix].portal_tag, &map[iy][ix].portal_coord
            //);

            //println!(
            //    "[{}, {}]",
            //    map[iy][ix].portal_coord.0.clone(),
            //    map[iy][ix].portal_coord.1.clone()
            //);
            //ix = map[iy][ix].portal_coord.0.clone();
            //println!(
            //    "[{}, {}]",
            //    map[iy][ix].portal_coord.0.clone(),
            //    map[iy][ix].portal_coord.1.clone()
            //);
            //iy = map[iy][ix].portal_coord.1.clone();
            //println!(
            //    "[{}, {}]",
            //    map[iy][ix].portal_coord.0.clone(),
            //    map[iy][ix].portal_coord.1.clone()
            //);

            //println!("[{}, {}], mod dist: {}", ix, iy, map[iy][ix].dist);
        }

        let d2 = map[iy][ix].dist;

        if map[iy][ix].passable && (d2 == -1 || d2 > d + 1) {
            map[iy][ix].dist = d + 1;
            recursive_fill(map, ix, iy);
        }
    }
}

