    let mut end_coord: (usize, usize) = (0, 0);

    for y1 in 0..map.len() {
        for x1 in 0..map.last().unwrap().len() {
            let loc1 = map[y1][x1].clone();

            if loc1.is_portal {
                let mut pc1: (usize, usize) = (0, 0);
                for (dx, dy) in &offsets {
                    let ix = (x1 as i32) + dx;
                    let iy = (y1 as i32) + dy;
                    if iy > 0
                        && iy < map.len() as i32
                        && ix > 0
                        && ix < map.last().unwrap().len() as i32
                    {
                        let uy = iy as usize;
                        let ux = ix as usize;
                        if map[uy][ux].passable {
                            pc1 = (ux, uy);
                        }
                    }
                }

                if loc1.portal_tag == "AA" {
                    start_coord = pc1;
                }
                if loc1.portal_tag == "ZZ" {
                    end_coord = pc1;
                }

                for y2 in 0..map.len() {
                    for x2 in 0..map.last().unwrap().len() {
                        let loc2 = &map[y2][x2].clone();

                        if !(x1 == x2 && y1 == y2)
                            && &loc1.portal_tag == &loc2.portal_tag
                            && loc2.is_portal
                        {
                            let mut pc2: (usize, usize) = (0, 0);
                            for (dx, dy) in &offsets {
                                let ix = (x2 as i32) + dx;
                                let iy = (y2 as i32) + dy;
                                if iy > 0
                                    && iy < map.len() as i32
                                    && ix > 0
                                    && ix < map.last().unwrap().len() as i32
                                {
                                    let uy = iy as usize;
                                    let ux = ix as usize;
                                    if map[uy][ux].passable {
                                        pc2 = (ux, uy);
                                    }
                                }
                            }

                            map[y1][x1].portal_coord = pc2;
                            map[y2][x2].portal_coord = pc1;

                            // println!("tag: {}, pc2: {:?}, pc1: {:?}", &loc1.portal_tag, pc1, pc2);
                        }
                    }
                }
            }
        }
    }

    map[start_coord.1][start_coord.0].dist = 0;
    recursive_fill(&mut map, start_coord.0, start_coord.1);

    println!("res: {}", map[end_coord.1][end_coord.0].dist);

